    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
//Incializar 
int num1, num2,total;

//Patsear de String a Int
num1=Integer.parseInt(inum1.getText());
num2=Integer.parseInt(inum2.getText());

//Summar total
total=num1+num2;

//Mostrar Total
itotal.setText(String.valueOf(total));
    }//GEN-LAST:event_jButton1ActionPerformed

